import { clear } from "@testing-library/user-event/dist/types/utility";

export default function Card({ scoop, basket, setBasket }) {
  // sepetteki urun adetini bulma
  const found = basket.filter((item) => item.name === scoop.name);

  const amount = found.length;

  const handleReset = () => {
    const clear = basket.filter((item) => item.name !== scoop.name);

    setBasket(clear);
  };
  return (
    <>
      <div
        className="d-flex flex-column align-items-center gap-3"
        style={{ width: "160px" }}
      >
        <img className="img-fluid" src={scoop.imagePath} />
        <label className="lead">{scoop.name}</label>
        <div className="d-flex align-items-center gap-2">
          <button className="btn btn-danger" onClick={handleReset}>
            Sifirla
          </button>
          <span className="lead">{amount}</span>
          <button
            className="btn btn-warning"
            onClick={() => setBasket([...basket, scoop])}
          >
            Ekle
          </button>
        </div>
      </div>
    </>
  );
}
