import axios from "axios";
import { useEffect, useState } from "react";

export default function toppings() {
  const [toppingsData, settoppingsData] = useState([]);
  const [basket, setBasket] = useState([]);

  useEffect(() => {
    axios
      .get("http://localhost:3030/toppings")
      .then((res) => settoppingsData(res.data));
  }, []);

  const handleCahnge = (e, topping) => {
    e.target.checked
      ? setBasket([...basket, topping])
      : setBasket(basket.filter((item) => item.name !== topping.name));
  };
  console.log(basket);
  return (
    <>
      <div className="container my-5">
        <h1>Sos Cesitleri</h1>
        <p>Tanesi: 3$</p>
        <h2>Soslar Ucreti: {basket.length * 3}$</h2>
        <div className="row gap-3 mt-3">
          {toppingsData.map((topping, i) => (
            <div
              key={i}
              className="d-flex flex-column align-items-center gap-2 "
              style={{ width: "150px" }}
            >
              <img className=" img-fluid" src={topping.imagePath} />
              <label htmlFor={topping.name} className="text-nowrap">
                {topping.name}
              </label>
              <input
                onChange={(e) => handleCahnge(e, topping)}
                className=" form-check-input"
                type="checkbox"
                id={topping.name}
              />
            </div>
          ))}
        </div>
      </div>
    </>
  );
}
